import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { employees } from './EmployeeList'; // Import EmployeeList
import '../styles/Login.css'; // Make sure this path is correct

const EmployeeLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Find employee by email
    const employee = employees.find(emp => emp.email === email);

    // Check if employee exists and if the password matches the default password
    if (employee && password === 'Employee123') {
      // Navigate to employee dashboard and optionally pass employeeId in local storage
      localStorage.setItem('employeeId', employee.id);
      localStorage.setItem('email', email);
      navigate('/employee-dashboard');
    } else {
      setError('Invalid login credentials');
    }
  };

  return (
    <div className="login-container">
      <h2>Employee Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <button type="submit">Login</button>
      </form>
      {error && <p className="error-message">{error}</p>}
    </div>
  );
};

export default EmployeeLogin;
