import React, { useState, useEffect } from "react";
import { getEmployees, deleteEmployee } from "../api/employeeService";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function EmployeeList() {
    const [employees, setEmployees] = useState([]);
    const [loadingId, setLoadingId] = useState(null); // Track loading state for individual deletions
    const navigate = useNavigate();

    useEffect(() => {
        // Fetch employee data on component mount
        async function fetchData() {
            try {
                const data = await getEmployees();
                setEmployees(data);
            } catch (error) {
                toast.error("Failed to fetch employees. Please try again later.");
            }
        }
        fetchData();
    }, []);

    const handleDelete = async (id) => {
        setLoadingId(id); // Set the specific employee as loading
        try {
            await deleteEmployee(id);
            setEmployees((prevEmployees) => prevEmployees.filter((emp) => emp.id !== id));
            toast.success("Employee deleted successfully.");
        } catch (error) {
            // Handle different error scenarios
            if (error.response?.status === 404) {
                toast.error(`Employee with ID ${id} not found.`);
            } else if (error.response?.status === 405) {
                toast.error("Method not allowed. Please check the server.");
            } else {
                toast.error("An unexpected error occurred.");
            }
        } finally {
            setLoadingId(null); // Reset loading state after deletion
        }
    };

    return (
        <div>
            <h1>Employee List</h1>
            <button onClick={() => navigate("/add")}>Add Employee</button>
            
            <table>
                <thead>
                    <tr>
                        <th>Name</th>console.log("Fetching employee data...");
console.log("Employees fetched:", data);
console.log("Deleting employee with ID:", id);
console.log("Employee deleted:", id);
console.log("Error deleting employee:", error);.log("Employee deleted:", id);
console.log("Error deleting employee:", error);
console.log("Error deleting employee:", error);
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((emp) => (
                        <tr key={emp.id}>
                            <td>{emp.name}</td>
                            <td>{emp.email}</td>
                            <td>{emp.phone}</td>
                            <td>
                                <button onClick={() => navigate(`/edit/${emp.id}`)}>Edit</button>
                                <button
                                    onClick={() => handleDelete(emp.id)}
                                    disabled={loadingId === emp.id} // Disable delete for the loading employee
                                >
                                    {loadingId === emp.id ? "Deleting..." : "Delete"}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* ToastContainer for global notifications */}
            <ToastContainer 
                position="top-right" 
                autoClose={3000} 
                hideProgressBar={false} 
                newestOnTop={false} 
                closeOnClick 
                rtl={false} 
                pauseOnFocusLoss 
                draggable 
                pauseOnHover 
            />
        </div>
    );
}

export default EmployeeList;
