import React, { useState } from 'react';
import axios from 'axios';

const EmployeeLogin = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // API call to authenticate employee
      const response = await axios.post('http://localhost:5000/api/employee/login', { email, password });
      const { employeeId } = response.data;

      // Store employeeId and redirect (or perform other actions)
      console.log(employeeId);
    } catch (error) {
      setError('Invalid login credentials');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <button type="submit">Login</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default EmployeeLogin;
