import React, { useState, useEffect } from "react";
import { getEmployees, deleteEmployee } from "../api/employeeService";
import { useNavigate } from "react-router-dom";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function EmployeeList() {
    const [employees, setEmployees] = useState([]);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            try {
                const data = await getEmployees();
                setEmployees(data);
            } catch (error) {
                toast.error("Failed to fetch employees. Please try again later.");
            }
        }
        fetchData();
    }, []);

    const handleDelete = async (id) => {
        setLoading(true);
        try {
            await deleteEmployee(id);
            setEmployees(employees.filter((emp) => emp.id !== id));
            toast.success("Employee deleted successfully.");
        } catch (error) {
            if (error.response && error.response.status === 404) {
                toast.error(`Employee with ID ${id} not found.`);
            } else if (error.response && error.response.status === 405) {
                toast.error("Method not allowed. Please check the server.");
            } else {
                toast.error("An unexpected error occurred.");
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <h1>Employee List</h1>
            <button onClick={() => navigate("/add")}>Add Employee</button>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((emp) => (
                        <tr key={emp.id}>
                            <td>{emp.name}</td>
                            <td>{emp.email}</td>
                            <td>{emp.phone}</td>
                            <td>
                                <button onClick={() => navigate(`/edit/${emp.id}`)}>Edit</button>
                                <button
                                    onClick={() => handleDelete(emp.id)}
                                    disabled={loading}
                                >
                                    {loading ? "Deleting..." : "Delete"}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <ToastContainer /> {/* Add this */}
        </div>
    );
}

export default EmployeeList;
