import React, { useEffect, useState } from "react";
import { getEmployees } from "../api/employeeService"; // Assuming this function fetches employees from backend
import { useNavigate } from "react-router-dom";
import { FaEdit, FaTrash, FaPlus } from 'react-icons/fa'; // Importing icons
import "react-toastify/dist/ReactToastify.css";
import '../styles/employeeList.css';
import { toast } from "react-toastify";

function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchEmployees() {
      try {
        const data = await getEmployees();
        setEmployees(data);  // Update the state with the fetched employees
      } catch (error) {
        toast.error("Failed to fetch employees. Please try again later.");
      }
    }

    fetchEmployees();  // Fetch employees when the component mounts
  }, []);

  return (
    <div>
      <h1>Employee List</h1>
      <button onClick={() => navigate("/add-employee")} className="add-button">
        <FaPlus /> Add Employee
      </button>
      <table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {employees.map((emp) => (
            <tr key={emp.id}>
              <td>{emp.id}</td>
              <td>{emp.name}</td>
              <td>{emp.email}</td>
              <td>{emp.phone}</td>
              <td>
                <button onClick={() => navigate(`/edit-employee/${emp.id}`)} className="edit-button">
                  <FaEdit /> Edit
                </button>
                <button className="delete-button">
                  <FaTrash /> Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default EmployeeList;
