import axios from "axios";

const instance = axios.create({
    baseURL: "http://localhost:8081", // Your Spring Boot base URL
});


const deleteEmployee = async (id) => {
    try {
        const response = await axios.delete(`http://localhost:8081/employees/${id}`);
        alert(`Success: ${response.data}`);
    } catch (error) {
        if (error.response) {
            // Server responded with a status code outside the range of 2xx
            if (error.response.status === 405) {
                alert("Error: Method not allowed. Please check the server.");
            } else if (error.response.status === 404) {
                alert(`Error: ${error.response.data}`);
            } else {
                alert(`Error: ${error.response.statusText}`);
            }
        } else if (error.request) {
            // Request was made but no response received
            console.error("No response received:", error.request);
            alert("Error: No response from the server. Please try again later.");
        } else {
            // Something happened in setting up the request
            console.error("Request error:", error.message);
            alert("Error: Could not complete the request. Please try again.");
        }
    }
};


export default instance;
