import React, { useEffect, useState } from "react";
import { getEmployees, deleteEmployee } from "../api/employeeService";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { FaEdit, FaTrash, FaPlus } from 'react-icons/fa'; // Importing icons
import "react-toastify/dist/ReactToastify.css";
import '../styles/employeeList.css';

function EmployeeList() {
    const [employees, setEmployees] = useState([]);
    const [loadingStates, setLoadingStates] = useState({});
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchData() {
            try {
                const data = await getEmployees();
                setEmployees(data);
            } catch (error) {
                toast.error("Failed to fetch employees. Please try again later.");
            }
        }
        fetchData();
    }, []);

    const handleDelete = async (id) => {
        setLoadingStates((prevState) => ({ ...prevState, [id]: true }));
        try {
            await deleteEmployee(id);
            setEmployees((prevEmployees) => prevEmployees.filter((emp) => emp.id !== id));
            toast.success("Employee deleted successfully.");
        } catch (error) {
            if (error.response?.status === 404) {
                toast.error(`Employee with ID ${id} not found.`);
            } else if (error.response?.status === 405) {
                toast.error("Method not allowed. Please check the server.");
            } else {
                toast.error("An unexpected error occurred.");
            }
        } finally {
            setLoadingStates((prevState) => ({ ...prevState, [id]: false }));
        }
    };

    const handleLogin = (email) => {
        // Assuming password is hardcoded or fetched from a database
        const defaultPassword = "Employee123"; // You can change this logic to fetch password from your API

        // Check if the employee exists with the provided email
        const employee = employees.find(emp => emp.email === email);
        if (employee && defaultPassword === "Employee123") {
            // Successful login, storing employee info in localStorage
            localStorage.setItem('employeeId', employee.id);
            localStorage.setItem('email', email);
            navigate("/employee-dashboard");
        } else {
            toast.error("Invalid login credentials");
        }
    };

    return (
        <div>
            <h1>Employee List</h1>
            
            <table>
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {employees.map((emp) => (
                        <tr key={emp.id}>
                            <td>{emp.id}</td>
                            <td>{emp.name}</td>
                            <td>{emp.email}</td>
                            <td>{emp.phone}</td>
                            <td className="btnalign">
                                <button onClick={() => navigate("/add")} className="add-button">
                                    <FaPlus /> Add Employee
                                </button>
                                <button
                                    onClick={() => handleDelete(emp.id)}
                                    disabled={loadingStates[emp.id] || false}
                                    className="delete-button">
                                    {loadingStates[emp.id] ? "Deleting..." : <><FaTrash /> Delete</>}
                                </button>
                                {/* Add login button for employee */}
                                <button
                                    onClick={() => handleLogin(emp.email)}
                                    className="login-button">
                                    Login
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default EmployeeList;
