import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getLoggedInEmployee } from "../api/employeeService";
import { toast } from "react-toastify";

function EmployeeProfile() {
    const [employees, setEmployees] = useState([]);
    const [loadingStates, setLoadingStates] = useState({});
    const navigate = useNavigate();


    useEffect(() => {
        async function fetchData() {
            try {
                const data = await getEmployees();
                setEmployees(data);
            } catch (error) {
                toast.error("Failed to fetch employees. Please try again later.");
            }
        }
        fetchData();
    }, []);

    return (
        <div>
            <h1>My Profile</h1>
            {employees ? (
                <div>
                    <p><strong>Name:</strong> {employees.name}</p>
                    <p><strong>Email:</strong> {employees.email}</p>
                    <p><strong>Phone:</strong> {employee.phone}</p>
                    <button onClick={() => navigate(`/edit/${employees.id}`)} className="edit-button">
                        Edit My Info
                    </button>
                </div>
            ) : (
                <p>No data available</p>
            )}
        </div>
    );
}

export default EmployeeProfile;
