// src/components/HomePage.js

import React from "react";
import { useNavigate } from "react-router-dom";
import '../styles/HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();

  const goToAdminLogin = () => {
    navigate("/admin-login"); // Navigate to the Admin Login page
  };

  const goToEmployeeLogin = () => {
    navigate("/employee-login"); // Navigate to the Employee Login page
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Welcome to the Login Portal</h1>
      <p>Please choose your login type</p>
      <div>
        <button onClick={goToAdminLogin}>Admin Login</button>
        <button onClick={goToEmployeeLogin} s>Employee Login</button>
      </div>
    </div>
  );
};

// Add some simple styling for buttons
const buttonStyle = {
  margin: "20px",
  padding: "10px 20px",
  fontSize: "16px",
  cursor: "pointer",
  backgroundColor: "#4CAF50",
  color: "white",
  border: "none",
  borderRadius: "5px",
};

export default HomePage;
