import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/formStyle.css';

const EditEmployee = () => {
  const { id } = useParams();
  const [employee, setEmployee] = useState({ name: '', email: '', phone: '' });

  useEffect(() => {
    // Fetch the employee details using the id from the API
    setEmployee({ name: 'John Doe', email: 'john@example.com', phone: '1234567890' });
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Update the employee data in the backend
    alert('Employee Updated!');
  };

  return (
    <div className="form-container">
      <h2>Edit Employee</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Name"
          value={employee.name}
          onChange={(e) => setEmployee({ ...employee, name: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={employee.email}
          onChange={(e) => setEmployee({ ...employee, email: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Phone"
          value={employee.phone}
          onChange={(e) => setEmployee({ ...employee, phone: e.target.value })}
          required
        />
        <button type="submit">Update Employee</button>
      </form>
    </div>
  );
};

export default EditEmployee;
