import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Use Routes instead of Switch

// Importing Components for Routing
import AdminLogin from './components/AdminLogin';
import EmployeeLogin from './components/EmployeeLogin';
import AdminDashboard from './components/AdminDashboard';
import EmployeeDashboard from './components/EmployeeDashboard';
import EmployeeList from './components/EmployeeList';
import AddEmployee from './components/AddEmployee';
import EditEmployee from './components/EditEmployee';
import TaskAssignment from './components/TaskAssignment';
import TaskStatusUpdate from './components/TaskStatusUpdate';

import './App.css'; // Import CSS for styling

const App = () => {
  const [isAdmin, setIsAdmin] = useState(false); // Flag for checking if admin is logged in

  // Function to handle admin login
  const handleAdminLogin = () => {
    setIsAdmin(true); // Set admin status to true after successful login
  };

  return (
    <Router>
      <div className="App">
        <Routes> {/* Updated from Switch to Routes */}
          {/* Admin Login Route */}
          <Route path="/AdminLogin" element={<AdminLogin handleAdminLogin={handleAdminLogin} />} />

          {/* Employee Login Route */}
          <Route path="/employee-login" element={<EmployeeLogin />} />

          {/* Admin Dashboard */}
          <Route path="/admin-dashboard" element={isAdmin ? <AdminDashboard /> : <AdminLogin />} />

          {/* Employee Dashboard */}
          <Route path="/employee-dashboard" element={<EmployeeDashboard />} />

          {/* Employee List */}
          <Route path="/employee-list" element={<EmployeeList />} />

          {/* Add Employee */}
          <Route path="/add-employee" element={<AddEmployee />} />

          {/* Edit Employee */}
          <Route path="/edit-employee" element={<EditEmployee />} />

          {/* Task Assignment */}
          <Route path="/task-assignment" element={<TaskAssignment />} />

          {/* Task Status Update */}
          <Route path="/task-status-update" element={<TaskStatusUpdate />} />

          {/* Redirect or fallback */}
          <Route path="/" element={<h1>Welcome to the Admin-Employee System</h1>} />
        </Routes> {/* Updated from Switch to Routes */}
      </div>
    </Router>
  );
};

export default App;
